import _ from 'lodash';
import { GET_CURRENT_USER } from '../actions';

export default function currentUserReducer(state = {}, action) {
  if (state === undefined) {
    return [];
  }

  switch (action.type) {
    case GET_CURRENT_USER:
      return action.payload;
    default:
      return state;
  }
}
