// Core React and style
import React, { Component } from 'react';
import './style.scss';
//Components
import NavBar from '../../components/navbar/navbar.jsx';
// Redux
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { Link } from 'react-router-dom';
import _ from 'lodash';
// Actions
import { fetchAllUsers } from '../../actions/index.js';

class Swiper extends Component {
  constructor(props) {
    super(props)
  }

  state = {
    users: this.props.users,
    currentMatchId: 0,
    currentUserId: 0
  }

  componentDidMount() {
    this.props.fetchAllUsers();
  }

  renderUsers() {
    return _.map(this.props.users, (user, index) => {
      if (user.matched === false && this.props.users.indexOf(user) === this.state.currentMatchId) {
      return (
      <div key={index+1} className="match-container">
        <h3>{user.first_name}</h3>
        <div className="image-container">
          <Link to={`/match_profile/${user.id-1}`} key={user.id}>
            {user.images.map((url,index) => <img skey={index+1} src={url} alt=""/>)}
          </Link>
        </div>
        <div className="about-container">
          <p id="bio"> {user.first_name}</p>
          <p>{user.last_name}</p>
          <p>{user.age} years old</p>
        </div>
      </div>
      )}
    })
  }

  toggleMatch = () => {
    console.log(this.state)
    this.setState({currentMatchId: this.state.currentMatchId + 1})
  }

  render() {
    const matchLink = document.getElementById('bio').innerText()
    console.log(matchLink)

    return(
      <div className='swiper'>
        <NavBar/>
        <div className='buttons'>
          <button onClick={this.toggleMatch}>Don't Match</button>
          <Link to='match_profile/1'>More Info</Link>
          <button>Match</button>
        </div>
        {this.renderUsers()}
      </div>
    )
  }
}

function mapStateToProps(state) {
  return {
    users: state.users,
    currentUser: state.currentUser
  };
}

function mapDispatchToProps(dispatch) {
  return bindActionCreators({ fetchAllUsers }, dispatch);
}

export default connect(mapStateToProps, mapDispatchToProps)(Swiper);
